    private void corregirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_corregirActionPerformed
        
        if (recarga1.isSelected()) {
            String cadena = totalPines.getText();
            String dinero = entrada.getText();
            int uno = Integer.parseInt(cadena);
            int dolares = Integer.parseInt(dinero);
            int rec1 = uno + 1;
            int dine1 = dolares - 1; 
            totalPines.setText(String.valueOf(rec1));
            entrada.setText (String.valueOf(dine1));
            JOptionPane.showMessageDialog(null, "REALIZO CORRECCION CON EXITO $1" );
            
        }else if (recarga3.isSelected()) {
            String cadena = totalPines.getText();
            String dinero = entrada.getText();
            int uno = Integer.parseInt(cadena);
            int dolares = Integer.parseInt(dinero);
            int rec1 = uno + 3;
            int dine1 = dolares - 3; 
            totalPines.setText(String.valueOf(rec1));
            entrada.setText (String.valueOf(dine1));
            JOptionPane.showMessageDialog(null, "REALIZO CORRECCION CON EXITO $3");
        }else if (recarga5.isSelected()) {
            String cadena = totalPines.getText();
            String dinero = entrada.getText();
            int uno = Integer.parseInt(cadena);
            int dolares = Integer.parseInt(dinero);
            int rec1 = uno + 5;
            int dine1 = dolares - 5; 
            totalPines.setText(String.valueOf(rec1));
            entrada.setText (String.valueOf(dine1));
            JOptionPane.showMessageDialog(null, "REALIZO CORRECCION CON EXITO $5");
        }else if (recarga6.isSelected()) {
            String cadena = totalPines.getText();
            String dinero = entrada.getText();
            int uno = Integer.parseInt(cadena); // CONVIERTE EL STRING A INT
            int dolares = Integer.parseInt(dinero);
            int rec1 = uno + 6;
            int dine1 = dolares - 6; 
            totalPines.setText(String.valueOf(rec1));
            entrada.setText (String.valueOf(dine1));
            JOptionPane.showMessageDialog(null, "REALIZO CORRECCION CON EXITO $6");
        }else if (recarga10.isSelected()) {
            String cadena = totalPines.getText();
            String dinero = entrada.getText();
            int uno = Integer.parseInt(cadena);
            int dolares = Integer.parseInt(dinero);
            int rec1 = uno + 10;
            int dine1 = dolares - 10; 
            totalPines.setText(String.valueOf(rec1));
            entrada.setText (String.valueOf(dine1));
            JOptionPane.showMessageDialog(null, "REALIZO CORRECCION CON EXITO $10");
        }
        
        
        
    }//GEN-LAST:event_corregirActionPerformed

